package test.com;

public class Test01for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 정해진 횟수, 길이만큼 반복 : for

		for (int i = 0; i < 19; i++) {
			System.out.println("배고프다 " + i);
		} // end of for

		System.out.println();

		for (byte i = 0; i < 19; i++) {
			System.out.println("배고프다 " + i);
		} // end of for

		System.out.println();

		for (char i = 0; i < 'b'; i++) {
			System.out.println("배고프다 " + i);
		} // end of for

	} // end of main

} // end of class
